import React, { Component } from 'react';

class Carousel_product_item extends Component {
    constructor(props) {
        super(props);
        this.state = {  
            "values":[  
               {  
                    "img":"http://placehold.it/350x260",
                    "name":"Những loài chim biển",
                    "author":"Phú Tống",
                    "cost-sale":"53,000đ",
                    "cost":"70,000đ"

               },
               {  
                    "img":"http://placehold.it/350x260",
                    "name":"Những loài chim biển",
                    "author":"Phú Tống",
                    "cost-sale":"53,000đ",
                    "cost":"70,000đ"
               },
               {  
                    "img":"http://placehold.it/350x260",
                    "name":"Những loài chim biển",
                    "author":"Phú Tống",
                    "cost-sale":"53,000đ",
                    "cost":"70,000đ"
               },
               {  
                    "img":"http://placehold.it/350x260",
                    "name":"Những loài chim biển",
                    "author":"Phú Tống",
                    "cost-sale":"53,000đ",
                    "cost":"70,000đ"
               },
               {  
                    "img":"http://placehold.it/350x260",
                    "name":"Những loài chim biển",
                    "author":"Phú Tống",
                    "cost-sale":"53,000đ",
                    "cost":"70,000đ"
               },
               {  
                    "img":"http://placehold.it/350x260",
                    "name":"Những loài chim biển",
                    "author":"Phú Tống",
                    "cost-sale":"53,000đ",
                    "cost":"70,000đ"
               }
            ]
         }
        
    }
    render() {
        return (
            <div className="carousel-inner">
                        <div className="carousel-item active">
                        <div className="row">
                        <div className="col-xs-4 col-sm-3 col-md-3 col-lg-3">
                            <div className="col-item">
                                <div className="photo">
                                <img src={this.props.img} className="img-fluid" alt="a" />
                                </div>
                                <div className="info">
                                <a href className="title-book">{this.props.name}</a><br />
                                <a href className="author-book link-secondary">{this.props.author}</a><br />
                                <div>
                                    <span><b className="cost-sale text-warning">{this.props.cost_sale}</b>
                                    </span>
                                    <span className="cost-book mx-2">
                                    <del>{this.props.cost}</del>
                                    </span>
                                    <span className="float-end"><h3><a href className="fa-solid fa-cart-plus" /></h3></span>
                                </div>
                                <div className="clearfix">
                                </div>
                                </div>
                            </div>
                            </div>
                        </div>
                        </div>
                    </div>
            
        );
    }
}

export default Carousel_product_item;
