import React, { Component } from 'react';

class Book_SGK_Item extends Component {
    render() {
        return (
            <div className=" info-product col-xs-6 col-sm-4 col-md-4 col-lg-4  pt-3 mt-2">
                        <a href><img src="../img/1gvhh.png" alt="" className="img-thumbnail" /></a>
                        <a href className="title-book">Sách Giáo Khoa</a><br />
                        <a href className="author-book link-secondary">Phú Tống</a><br />
                        <div>
                            <span><b className="cost-sale text-warning">710,000đ</b>
                            </span>
                            <span className="cost-book mx-2">
                            <del>750,000đ</del>
                            </span>
                            <span className="float-end"><h3><a href className="fa-solid fa-cart-plus" /></h3></span>
                        </div>
            </div>
        );
    }
}

export default Book_SGK_Item;
