import React, { Component } from 'react';
import Carousel_product_item from './Carousel_product_item';

class Carousel_product extends Component {
    constructor(props) {
        super(props);
        this.state = {  
            "values":[  
               {  
                  "img":0,
                  "name":"Jeff",
                  "author":"",
                  "cost-sale":"",
                  "cost":"",

               },
               {  
                  "id":1,
                  "name":"Joe"
               },
               {  
                  "id":2,
                  "name":"John"
               },
               {  
                  "id":3,
                  "name":"Billy"
               },
               {  
                  "id":4,
                  "name":"Horace"
               },
               {  
                  "id":5,
                  "name":"Greg"
               }
            ]
         }
        
    }
    render() {
        return (
                <div className="container mt-5">
                <div className="row">
                    <div id="carouselProducts" className="carousel slide" data-bs-ride="carousel">
                    <div className="carousel-inner">
                        <div className="carousel-item active">
                        <div className="row">
                            <Carousel_product_item 
                             img ="http://placehold.it/350x260"
                             name = ""
                             />
                            <Carousel_product_item />
                            <Carousel_product_item />
                            <Carousel_product_item />
                        </div>
                        </div>
                        <div className="carousel-item">
                        <div className="row">
                            <Carousel_product_item />
                            <Carousel_product_item />
                            <Carousel_product_item />
                            <Carousel_product_item />
                        </div>
                        </div>
                    </div>
                    <div className="carousel-item">
                        <div className="row">
                            <Carousel_product_item />
                            <Carousel_product_item />
                            <Carousel_product_item />
                            <Carousel_product_item />
                        </div>
                    </div>
                    </div>
                </div>
                
                </div>
        );
    }
}

export default Carousel_product;
