import React, { Component } from 'react';
import Carousel_product_item from './Carousel_product_item';

class Carousel_product extends Component {
    constructor(props) {
        super(props);
        this.state = {
            "SGK": [
                {
                    "img": "http://placehold.it/350x260",
                    "name": "Những loài chim biển",
                    "author": "Phú Tống",
                    "cost_sale": "53,000đ",
                    "cost": "70,000đ"
                },
                {
                    "img": "http://placehold.it/350x260",
                    "name": "Những loài chim thiên nga",
                    "author": "Phú Tống",
                    "cost_sale": "53,000đ",
                    "cost": "70,000đ"
                },
                {
                    "img": "http://placehold.it/350x260",
                    "name": "Những loài chim biển",
                    "author": "Phú Tống",
                    "cost_sale": "53,000đ",
                    "cost": "70,000đ"
                },
                {
                    "img": "http://placehold.it/350x260",
                    "name": "Những loài chim biển",
                    "author": "Phú Tống",
                    "cost_sale": "53,000đ",
                    "cost": "70,000đ"
                }
            ]
        }

    }
    render() {
        var SGK = this.state.SGK.map((value, index) => {
            return <Carousel_product_item
                        img={value.img}
                        name={value.name}
                        author={value.author}
                        cost_sale={value.cost_sale}
                        cost={value.cost}
                    />
        });
        return (
            <div className="container mt-5">
                <div className="row">
                    <div id="carouselProducts" className="carousel slide" data-bs-ride="carousel">
                        <div className="carousel-inner">
                            <div className="carousel-item active">
                                <div className="row">
                                    {SGK}    
                                </div>
                            </div>
                            <div className="carousel-item ">
                                <div className="row">
                                    {Product}    
                                </div>
                            </div>
                            <div className="carousel-item ">
                                <div className="row">
                                    {Product}    
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Carousel_product;
