                        </div>
                        </div>
                    </div>
                    <div className="carousel-item">
                        <div className="row">
                            <Carousel_product_item />
                            <Carousel_product_item />
                            <Carousel_product_item />
                            <Carousel_product_item />
                        </div>
                    </div>
                    </div>
                </div>
                
                </div>
        );
    }
}

