import React, { Component } from 'react';

class Carousel_product_item extends Component {
    render() {
        return (
            <div className="col-xs-4 col-sm-3 col-md-3 col-lg-3">
                            <div className="col-item">
                                <div className="photo">
                                <img src="http://placehold.it/350x260" className="img-fluid" alt="a" />
                                </div>
                                <div className="info">
                                <a href className="title-book">Sách Khám Phá Khoa Học</a><br />
                                <a href className="author-book link-secondary">Phú Tống</a><br />
                                <div>
                                    <span><b className="cost-sale text-warning">700,000đ</b>
                                    </span>
                                    <span className="cost-book mx-2">
                                    <del>750,000đ</del>
                                    </span>
                                    <span className="float-end"><h3><a href className="fa-solid fa-cart-plus" /></h3></span>
                                </div>
                                <div className="clearfix">
                                </div>
                                </div>
                            </div>
                            </div>
        );
    }
}

export default Carousel_product_item;
