import React, { Component } from 'react';
import Carousel_product_item from './Carousel_product_item';

class Carousel_product extends Component {
    constructor(props) {
        super(props);
        this.state = {
            "values": [
                {
                    "img": "http://placehold.it/350x260",
                    "name": "Những loài chim biển",
                    "author": "Phú Tống",
                    "cost-sale": "53,000đ",
                    "cost": "70,000đ"

                },
                {
                    "img": "http://placehold.it/350x260",
                    "name": "Những loài chim biển",
                    "author": "Phú Tống",
                    "cost-sale": "53,000đ",
                    "cost": "70,000đ"
                },
                {
                    "img": "http://placehold.it/350x260",
                    "name": "Những loài chim biển",
                    "author": "Phú Tống",
                    "cost-sale": "53,000đ",
                    "cost": "70,000đ"
                },
                {
                    "img": "http://placehold.it/350x260",
                    "name": "Những loài chim thiên nga",
                    "author": "Phú Tống",
                    "cost-sale": "53,000đ",
                    "cost": "70,000đ"
                },
                {
                    "img": "http://placehold.it/350x260",
                    "name": "Những loài chim biển",
                    "author": "Phú Tống",
                    "cost-sale": "53,000đ",
                    "cost": "70,000đ"
                },
                {
                    "img": "http://placehold.it/350x260",
                    "name": "Những loài chim biển",
                    "author": "Phú Tống",
                    "cost-sale": "53,000đ",
                    "cost": "70,000đ"
                }
            ]
        }

    }
    render() {
        var Product = this.state.values.map((value,index)=>{
            return <div key={index} className="carousel-item" className={index===0?"active" : ""}>
                    <div className="row">
                        <Carousel_product_item
                            img="http://placehold.it/350x260"
                            name=""
                            author=""
                            cost_sale=""
                            cost =""
                        />
                    </div>
                </div>
        });
        return (
            <div className="container mt-5">
                <div className="row">
                    <div id="carouselProducts" className="carousel slide" data-bs-ride="carousel">
                        <div className="carousel-inner">
                            <div className="carousel-item active">
                                <div className="row">
                                    <Carousel_product_item
                                        img="http://placehold.it/350x260"
                                        name=""
                                    />
                                    <Carousel_product_item />
                                    <Carousel_product_item />
                                    <Carousel_product_item />
                                </div>
                            </div>
                    </div>
                </div>

            </div>
            </div>
        );
    }
}

export default Carousel_product;
